#include <iostream>int main() {    int n;    int w[201];    scanf("%d", &n);    int allSum = 0;    int allSum_true;    for (int i = 0; i < n; ++i) {        scanf("%d", &w[i]);        allSum += w[i];    }    allSum_true = allSum;    allSum += allSum % 2;    allSum /= 2;    int dp[n + 1][allSum + 1];    for (int i = 0; i < n + 1; i++) {        for (int j = 0; j < allSum + 1; ++j) {            dp[i][j] = 0;        }    }    for (int i = 1; i <= n; ++i) {        for (int j = 1; j <= allSum; ++j) {            int t1 = dp[i - 1][j];            int t2 = 0;            if (j >= w[i - 1]) {                t2 = dp[i - 1][j - w[i - 1]] + w[i - 1];            }            dp[i][j] = (t1 > t2) ? t1 : t2;        }    }    int result = allSum_true - 2 * dp[n][allSum];    result = (result > 0) ? result : -result;    printf("%d", result);    return 0;}