#include<stdio.h>int main() {    int n, m;    scanf("%d %d", &n, &m);    int c[21] = {0};    c[0] = 1;    for (int i = 1; i <= n; ++i) {        int temp;        scanf("%d", &temp);        c[i] = c[i - 1] * temp;    }    int addi = 0;    for (int i = 1; i <= n; ++i) {        int result = m % c[i] - addi;        addi += result;        printf("%d ", result / c[i - 1]);    }    return 0;}