#include<stdio.h>#include <cmath>int main() {    int n;    scanf("%d", &n);    double mean = 0;    double m = 0;    int inputs[n];    for (int i = 0; i < n; ++i) {        int temp;        scanf("%d", &temp);        mean += temp * temp * 1.0 / n * 1.0;        m += temp * 1.0 / n * 1.0;        inputs[i] = temp;    }    double D = mean - m * m;    for (int i = 0; i < n; ++i) {        double result = (inputs[i] - m) / (sqrt(D));        printf("%f\n", result);    }    return 0;}