#include <iostream>#include <string>#include <vector>struct sol {    std::string l;    int count;};int main() {    int n;    scanf("%d", &n);    char a[10];    std::vector<sol> b;    for (int i = 0; i < n; ++i) {        std::string loc;        for (int j = 0; j < 8; ++j) {            do {                gets(a);            } while (a[0] == 0);            loc = loc + a;        }        bool find = false;        int ssssss = 0;        for (auto it = b.begin(); it != b.end(); it++) {            if (it->l == loc) {                it->count++;                find = true;                ssssss = it->count;                break;            }        }        if (!find) {            b.push_back({loc, 1});            ssssss = 1;        }        printf("%d\n", ssssss);    }    return 0;}